import React, { Component } from "react";
class Todos extends Component {
  state = { newtodo: "", todolist: [] };
  doSubmit = () => {
    const newtodo = { value: this.state.newtodo };
    const listtodo = [...this.state.todolist];
    listtodo.push(newtodo);
    this.setState({ todolist: listtodo });
  };
  handleChange = (e) => {
    this.setState({ newtodo: e.target.value });
  };
  render() {
    return (
      <div>
        <input
          type="text"
          name="newtodo"
          value={this.state.newtodo}
          onChange={this.handleChange}
        ></input>
        <button onClick={() => this.doSubmit()}>Add</button>
        <ul>
          {this.state.todolist.map((list) => (
            <il>{list}</il>
          ))}
        </ul>
      </div>
    );
  }
}

export default Todos;
